import { useState } from 'react';
import { Plus, BookOpen, Pencil, Trash2, Euro } from 'lucide-react';
import { format, parseISO } from 'date-fns';
import { de } from 'date-fns/locale';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { ConfirmDialog } from '@/components/ConfirmDialog';
import { EmptyState } from '@/components/EmptyState';
import { LivingAppsService, extractRecordId, createRecordUrl } from '@/services/livingAppsService';
import { APP_IDS } from '@/types/app';
import type { Kurse, Dozenten, Raeume } from '@/types/app';

interface KurseTabProps {
  kurse: Kurse[];
  dozenten: Dozenten[];
  raeume: Raeume[];
  onRefresh: () => void;
}

export function KurseTab({ kurse, dozenten, raeume, onRefresh }: KurseTabProps) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteDialog, setDeleteDialog] = useState<{ open: boolean; id: string | null }>({ open: false, id: null });
  const [editingId, setEditingId] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    titel: '',
    beschreibung: '',
    startdatum: '',
    enddatum: '',
    max_teilnehmer: '',
    preis: '',
    dozent: '',
    raum: '',
  });

  const resetForm = () => {
    setForm({
      titel: '',
      beschreibung: '',
      startdatum: '',
      enddatum: '',
      max_teilnehmer: '',
      preis: '',
      dozent: '',
      raum: '',
    });
    setEditingId(null);
  };

  const handleOpenDialog = (k?: Kurse) => {
    if (k) {
      setEditingId(k.record_id);
      setForm({
        titel: k.fields.titel || '',
        beschreibung: k.fields.beschreibung || '',
        startdatum: k.fields.startdatum || '',
        enddatum: k.fields.enddatum || '',
        max_teilnehmer: k.fields.max_teilnehmer?.toString() || '',
        preis: k.fields.preis?.toString() || '',
        dozent: extractRecordId(k.fields.dozent) || '',
        raum: extractRecordId(k.fields.raum) || '',
      });
    } else {
      resetForm();
    }
    setDialogOpen(true);
  };

  const handleSubmit = async () => {
    if (!form.titel || !form.startdatum || !form.enddatum || !form.max_teilnehmer || !form.preis || !form.dozent || !form.raum) return;
    setLoading(true);
    try {
      const data = {
        titel: form.titel,
        beschreibung: form.beschreibung || undefined,
        startdatum: form.startdatum,
        enddatum: form.enddatum,
        max_teilnehmer: parseInt(form.max_teilnehmer, 10),
        preis: parseFloat(form.preis),
        dozent: createRecordUrl(APP_IDS.DOZENTEN, form.dozent),
        raum: createRecordUrl(APP_IDS.RAEUME, form.raum),
      };
      if (editingId) {
        await LivingAppsService.updateKurseEntry(editingId, data);
      } else {
        await LivingAppsService.createKurseEntry(data);
      }
      setDialogOpen(false);
      resetForm();
      onRefresh();
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!deleteDialog.id) return;
    setLoading(true);
    try {
      await LivingAppsService.deleteKurseEntry(deleteDialog.id);
      setDeleteDialog({ open: false, id: null });
      onRefresh();
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateStr?: string) => {
    if (!dateStr) return '–';
    try {
      return format(parseISO(dateStr), 'dd.MM.yyyy', { locale: de });
    } catch {
      return dateStr;
    }
  };

  const getDozentName = (dozentUrl?: string) => {
    const id = extractRecordId(dozentUrl);
    if (!id) return '–';
    const dozent = dozenten.find(d => d.record_id === id);
    return dozent?.fields.name || '–';
  };

  const getRaumName = (raumUrl?: string) => {
    const id = extractRecordId(raumUrl);
    if (!id) return '–';
    const raum = raeume.find(r => r.record_id === id);
    return raum ? `${raum.fields.raumname} (${raum.fields.gebaeude})` : '–';
  };

  const isFormValid = form.titel && form.startdatum && form.enddatum && form.max_teilnehmer && form.preis && form.dozent && form.raum;

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold text-foreground">Kurse</h2>
          <p className="text-sm text-muted-foreground">Verwalten Sie Ihre Kursangebote</p>
        </div>
        <Button onClick={() => handleOpenDialog()}>
          <Plus className="size-4" />
          Kurs hinzufügen
        </Button>
      </div>

      {kurse.length === 0 ? (
        <EmptyState
          icon={<BookOpen className="size-8" />}
          title="Keine Kurse"
          description="Fügen Sie Ihren ersten Kurs hinzu, um loszulegen."
          action={
            <Button onClick={() => handleOpenDialog()}>
              <Plus className="size-4" />
              Kurs hinzufügen
            </Button>
          }
        />
      ) : (
        <div className="data-table-container">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Titel</TableHead>
                <TableHead>Zeitraum</TableHead>
                <TableHead>Dozent</TableHead>
                <TableHead>Raum</TableHead>
                <TableHead>Max. TN</TableHead>
                <TableHead>Preis</TableHead>
                <TableHead className="w-24"></TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {kurse.map((k) => (
                <TableRow key={k.record_id}>
                  <TableCell className="font-medium">{k.fields.titel}</TableCell>
                  <TableCell className="text-muted-foreground">
                    {formatDate(k.fields.startdatum)} – {formatDate(k.fields.enddatum)}
                  </TableCell>
                  <TableCell>{getDozentName(k.fields.dozent)}</TableCell>
                  <TableCell>{getRaumName(k.fields.raum)}</TableCell>
                  <TableCell>
                    <span className="badge-muted">{k.fields.max_teilnehmer}</span>
                  </TableCell>
                  <TableCell>
                    <span className="font-medium text-foreground">{k.fields.preis?.toFixed(2)} €</span>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1">
                      <Button variant="ghost" size="icon-sm" onClick={() => handleOpenDialog(k)}>
                        <Pencil className="size-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon-sm"
                        onClick={() => setDeleteDialog({ open: true, id: k.record_id })}
                      >
                        <Trash2 className="size-4 text-destructive" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}

      <Dialog open={dialogOpen} onOpenChange={(open) => { setDialogOpen(open); if (!open) resetForm(); }}>
        <DialogContent className="max-w-lg">
          <DialogHeader>
            <DialogTitle>{editingId ? 'Kurs bearbeiten' : 'Neuer Kurs'}</DialogTitle>
          </DialogHeader>
          <div className="form-section max-h-[60vh] overflow-y-auto pr-2">
            <div className="space-y-2">
              <Label htmlFor="kurs-titel">Titel *</Label>
              <Input
                id="kurs-titel"
                value={form.titel}
                onChange={(e) => setForm({ ...form, titel: e.target.value })}
                placeholder="z.B. Einführung in Python"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="kurs-beschreibung">Beschreibung</Label>
              <Textarea
                id="kurs-beschreibung"
                value={form.beschreibung}
                onChange={(e) => setForm({ ...form, beschreibung: e.target.value })}
                placeholder="Kursbeschreibung..."
                rows={3}
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="kurs-startdatum">Startdatum *</Label>
                <Input
                  id="kurs-startdatum"
                  type="date"
                  value={form.startdatum}
                  onChange={(e) => setForm({ ...form, startdatum: e.target.value })}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="kurs-enddatum">Enddatum *</Label>
                <Input
                  id="kurs-enddatum"
                  type="date"
                  value={form.enddatum}
                  onChange={(e) => setForm({ ...form, enddatum: e.target.value })}
                />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="kurs-max-teilnehmer">Max. Teilnehmer *</Label>
                <Input
                  id="kurs-max-teilnehmer"
                  type="number"
                  min="1"
                  value={form.max_teilnehmer}
                  onChange={(e) => setForm({ ...form, max_teilnehmer: e.target.value })}
                  placeholder="z.B. 20"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="kurs-preis">Preis (€) *</Label>
                <Input
                  id="kurs-preis"
                  type="number"
                  min="0"
                  step="0.01"
                  value={form.preis}
                  onChange={(e) => setForm({ ...form, preis: e.target.value })}
                  placeholder="z.B. 299.00"
                />
              </div>
            </div>
            <div className="space-y-2">
              <Label htmlFor="kurs-dozent">Dozent *</Label>
              <Select value={form.dozent} onValueChange={(v) => setForm({ ...form, dozent: v })}>
                <SelectTrigger id="kurs-dozent">
                  <SelectValue placeholder="Dozent auswählen" />
                </SelectTrigger>
                <SelectContent>
                  {dozenten.map((d) => (
                    <SelectItem key={d.record_id} value={d.record_id}>
                      {d.fields.name} {d.fields.fachgebiet ? `(${d.fields.fachgebiet})` : ''}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="kurs-raum">Raum *</Label>
              <Select value={form.raum} onValueChange={(v) => setForm({ ...form, raum: v })}>
                <SelectTrigger id="kurs-raum">
                  <SelectValue placeholder="Raum auswählen" />
                </SelectTrigger>
                <SelectContent>
                  {raeume.map((r) => (
                    <SelectItem key={r.record_id} value={r.record_id}>
                      {r.fields.raumname} ({r.fields.gebaeude}) – {r.fields.kapazitaet} Plätze
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setDialogOpen(false)}>Abbrechen</Button>
            <Button onClick={handleSubmit} disabled={loading || !isFormValid}>
              {loading ? 'Speichern...' : 'Speichern'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <ConfirmDialog
        open={deleteDialog.open}
        onOpenChange={(open) => setDeleteDialog({ open, id: open ? deleteDialog.id : null })}
        title="Kurs löschen"
        description="Möchten Sie diesen Kurs wirklich löschen? Alle zugehörigen Anmeldungen werden ebenfalls betroffen."
        onConfirm={handleDelete}
        loading={loading}
      />
    </div>
  );
}

toolUseResult.content:
import { useState } from 'react';
import { Plus, BookOpen, Pencil, Trash2, Euro } from 'lucide-react';
import { format, parseISO } from 'date-fns';
import { de } from 'date-fns/locale';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { ConfirmDialog } from '@/components/ConfirmDialog';
import { EmptyState } from '@/components/EmptyState';
import { LivingAppsService, extractRecordId, createRecordUrl } from '@/services/livingAppsService';
import { APP_IDS } from '@/types/app';
import type { Kurse, Dozenten, Raeume } from '@/types/app';

interface KurseTabProps {
  kurse: Kurse[];
  dozenten: Dozenten[];
  raeume: Raeume[];
  onRefresh: () => void;
}

export function KurseTab({ kurse, dozenten, raeume, onRefresh }: KurseTabProps) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteDialog, setDeleteDialog] = useState<{ open: boolean; id: string | null }>({ open: false, id: null });
  const [editingId, setEditingId] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    titel: '',
    beschreibung: '',
    startdatum: '',
    enddatum: '',
    max_teilnehmer: '',
    preis: '',
    dozent: '',
    raum: '',
  });

  const resetForm = () => {
    setForm({
      titel: '',
      beschreibung: '',
      startdatum: '',
      enddatum: '',
      max_teilnehmer: '',
      preis: '',
      dozent: '',
      raum: '',
    });
    setEditingId(null);
  };

  const handleOpenDialog = (k?: Kurse) => {
    if (k) {
      setEditingId(k.record_id);
      setForm({
        titel: k.fields.titel || '',
        beschreibung: k.fields.beschreibung || '',
        startdatum: k.fields.startdatum || '',
        enddatum: k.fields.enddatum || '',
        max_teilnehmer: k.fields.max_teilnehmer?.toString() || '',
        preis: k.fields.preis?.toString() || '',
        dozent: extractRecordId(k.fields.dozent) || '',
        raum: extractRecordId(k.fields.raum) || '',
      });
    } else {
      resetForm();
    }
    setDialogOpen(true);
  };

  const handleSubmit = async () => {
    if (!form.titel || !form.startdatum || !form.enddatum || !form.max_teilnehmer || !form.preis || !form.dozent || !form.raum) return;
    setLoading(true);
    try {
      const data = {
        titel: form.titel,
        beschreibung: form.beschreibung || undefined,
        startdatum: form.startdatum,
        enddatum: form.enddatum,
        max_teilnehmer: parseInt(form.max_teilnehmer, 10),
        preis: parseFloat(form.preis),
        dozent: createRecordUrl(APP_IDS.DOZENTEN, form.dozent),
        raum: createRecordUrl(APP_IDS.RAEUME, form.raum),
      };
      if (editingId) {
        await LivingAppsService.updateKurseEntry(editingId, data);
      } else {
        await LivingAppsService.createKurseEntry(data);
      }
      setDialogOpen(false);
      resetForm();
      onRefresh();
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!deleteDialog.id) return;
    setLoading(true);
    try {
      await LivingAppsService.deleteKurseEntry(deleteDialog.id);
      setDeleteDialog({ open: false, id: null });
      onRefresh();
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateStr?: string) => {
    if (!dateStr) return '–';
    try {
      return format(parseISO(dateStr), 'dd.MM.yyyy', { locale: de });
    } catch {
      return dateStr;
    }
  };

  const getDozentName = (dozentUrl?: string) => {
    const id = extractRecordId(dozentUrl);
    if (!id) return '–';
    const dozent = dozenten.find(d => d.record_id === id);
    return dozent?.fields.name || '–';
  };

  const getRaumName = (raumUrl?: string) => {
    const id = extractRecordId(raumUrl);
    if (!id) return '–';
    const raum = raeume.find(r => r.record_id === id);
    return raum ? `${raum.fields.raumname} (${raum.fields.gebaeude})` : '–';
  };

  const isFormValid = form.titel && form.startdatum && form.enddatum && form.max_teilnehmer && form.preis && form.dozent && form.raum;

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold text-foreground">Kurse</h2>
          <p className="text-sm text-muted-foreground">Verwalten Sie Ihre Kursangebote</p>
        </div>
        <Button onClick={() => handleOpenDialog()}>
          <Plus className="size-4" />
          Kurs hinzufügen
        </Button>
      </div>

      {kurse.length === 0 ? (
        <EmptyState
          icon={<BookOpen className="size-8" />}
          title="Keine Kurse"
          description="Fügen Sie Ihren ersten Kurs hinzu, um loszulegen."
          action={
            <Button onClick={() => handleOpenDialog()}>
              <Plus className="size-4" />
              Kurs hinzufügen
            </Button>
          }
        />
      ) : (
        <div className="data-table-container">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Titel</TableHead>
                <TableHead>Zeitraum</TableHead>
                <TableHead>Dozent</TableHead>
                <TableHead>Raum</TableHead>
                <TableHead>Max. TN</TableHead>
                <TableHead>Preis</TableHead>
                <TableHead className="w-24"></TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {kurse.map((k) => (
                <TableRow key={k.record_id}>
                  <TableCell className="font-medium">{k.fields.titel}</TableCell>
                  <TableCell className="text-muted-foreground">
                    {formatDate(k.fields.startdatum)} – {formatDate(k.fields.enddatum)}
                  </TableCell>
                  <TableCell>{getDozentName(k.fields.dozent)}</TableCell>
                  <TableCell>{getRaumName(k.fields.raum)}</TableCell>
                  <TableCell>
                    <span className="badge-muted">{k.fields.max_teilnehmer}</span>
                  </TableCell>
                  <TableCell>
                    <span className="font-medium text-foreground">{k.fields.preis?.toFixed(2)} €</span>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1">
                      <Button variant="ghost" size="icon-sm" onClick={() => handleOpenDialog(k)}>
                        <Pencil className="size-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon-sm"
                        onClick={() => setDeleteDialog({ open: true, id: k.record_id })}
                      >
                        <Trash2 className="size-4 text-destructive" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}

      <Dialog open={dialogOpen} onOpenChange={(open) => { setDialogOpen(open); if (!open) resetForm(); }}>
        <DialogContent className="max-w-lg">
          <DialogHeader>
            <DialogTitle>{editingId ? 'Kurs bearbeiten' : 'Neuer Kurs'}</DialogTitle>
          </DialogHeader>
          <div className="form-section max-h-[60vh] overflow-y-auto pr-2">
            <div className="space-y-2">
              <Label htmlFor="kurs-titel">Titel *</Label>
              <Input
                id="kurs-titel"
                value={form.titel}
                onChange={(e) => setForm({ ...form, titel: e.target.value })}
                placeholder="z.B. Einführung in Python"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="kurs-beschreibung">Beschreibung</Label>
              <Textarea
                id="kurs-beschreibung"
                value={form.beschreibung}
                onChange={(e) => setForm({ ...form, beschreibung: e.target.value })}
                placeholder="Kursbeschreibung..."
                rows={3}
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="kurs-startdatum">Startdatum *</Label>
                <Input
                  id="kurs-startdatum"
                  type="date"
                  value={form.startdatum}
                  onChange={(e) => setForm({ ...form, startdatum: e.target.value })}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="kurs-enddatum">Enddatum *</Label>
                <Input
                  id="kurs-enddatum"
                  type="date"
                  value={form.enddatum}
                  onChange={(e) => setForm({ ...form, enddatum: e.target.value })}
                />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="kurs-max-teilnehmer">Max. Teilnehmer *</Label>
                <Input
                  id="kurs-max-teilnehmer"
                  type="number"
                  min="1"
                  value={form.max_teilnehmer}
                  onChange={(e) => setForm({ ...form, max_teilnehmer: e.target.value })}
                  placeholder="z.B. 20"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="kurs-preis">Preis (€) *</Label>
                <Input
                  id="kurs-preis"
                  type="number"
                  min="0"
                  step="0.01"
                  value={form.preis}
                  onChange={(e) => setForm({ ...form, preis: e.target.value })}
                  placeholder="z.B. 299.00"
                />
              </div>
            </div>
            <div className="space-y-2">
              <Label htmlFor="kurs-dozent">Dozent *</Label>
              <Select value={form.dozent} onValueChange={(v) => setForm({ ...form, dozent: v })}>
                <SelectTrigger id="kurs-dozent">
                  <SelectValue placeholder="Dozent auswählen" />
                </SelectTrigger>
                <SelectContent>
                  {dozenten.map((d) => (
                    <SelectItem key={d.record_id} value={d.record_id}>
                      {d.fields.name} {d.fields.fachgebiet ? `(${d.fields.fachgebiet})` : ''}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="kurs-raum">Raum *</Label>
              <Select value={form.raum} onValueChange={(v) => setForm({ ...form, raum: v })}>
                <SelectTrigger id="kurs-raum">
                  <SelectValue placeholder="Raum auswählen" />
                </SelectTrigger>
                <SelectContent>
                  {raeume.map((r) => (
                    <SelectItem key={r.record_id} value={r.record_id}>
                      {r.fields.raumname} ({r.fields.gebaeude}) – {r.fields.kapazitaet} Plätze
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setDialogOpen(false)}>Abbrechen</Button>
            <Button onClick={handleSubmit} disabled={loading || !isFormValid}>
              {loading ? 'Speichern...' : 'Speichern'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <ConfirmDialog
        open={deleteDialog.open}
        onOpenChange={(open) => setDeleteDialog({ open, id: open ? deleteDialog.id : null })}
        title="Kurs löschen"
        description="Möchten Sie diesen Kurs wirklich löschen? Alle zugehörigen Anmeldungen werden ebenfalls betroffen."
        onConfirm={handleDelete}
        loading={loading}
      />
    </div>
  );
}

input.content:
import { useState } from 'react';
import { Plus, ClipboardList, Pencil, Trash2, Check, X } from 'lucide-react';
import { format, parseISO } from 'date-fns';
import { de } from 'date-fns/locale';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { ConfirmDialog } from '@/components/ConfirmDialog';
import { EmptyState } from '@/components/EmptyState';
import { LivingAppsService, extractRecordId, createRecordUrl } from '@/services/livingAppsService';
import { APP_IDS } from '@/types/app';
import type { Anmeldungen, Teilnehmer, Kurse } from '@/types/app';

interface AnmeldungenTabProps {
  anmeldungen: Anmeldungen[];
  teilnehmer: Teilnehmer[];
  kurse: Kurse[];
  onRefresh: () => void;
}

export function AnmeldungenTab({ anmeldungen, teilnehmer, kurse, onRefresh }: AnmeldungenTabProps) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteDialog, setDeleteDialog] = useState<{ open: boolean; id: string | null }>({ open: false, id: null });
  const [editingId, setEditingId] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    teilnehmer: '',
    kurs: '',
    anmeldedatum: '',
    bezahlt: false,
  });

  const resetForm = () => {
    setForm({ teilnehmer: '', kurs: '', anmeldedatum: '', bezahlt: false });
    setEditingId(null);
  };

  const handleOpenDialog = (a?: Anmeldungen) => {
    if (a) {
      setEditingId(a.record_id);
      setForm({
        teilnehmer: extractRecordId(a.fields.teilnehmer) || '',
        kurs: extractRecordId(a.fields.kurs) || '',
        anmeldedatum: a.fields.anmeldedatum || '',
        bezahlt: a.fields.bezahlt || false,
      });
    } else {
      // Set today as default registration date
      const today = new Date().toISOString().split('T')[0];
      setForm({ teilnehmer: '', kurs: '', anmeldedatum: today, bezahlt: false });
      setEditingId(null);
    }
    setDialogOpen(true);
  };

  const handleSubmit = async () => {
    if (!form.teilnehmer || !form.kurs || !form.anmeldedatum) return;
    setLoading(true);
    try {
      const data = {
        teilnehmer: createRecordUrl(APP_IDS.TEILNEHMER, form.teilnehmer),
        kurs: createRecordUrl(APP_IDS.KURSE, form.kurs),
        anmeldedatum: form.anmeldedatum,
        bezahlt: form.bezahlt,
      };
      if (editingId) {
        await LivingAppsService.updateAnmeldungenEntry(editingId, data);
      } else {
        await LivingAppsService.createAnmeldungenEntry(data);
      }
      setDialogOpen(false);
      resetForm();
      onRefresh();
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!deleteDialog.id) return;
    setLoading(true);
    try {
      await LivingAppsService.deleteAnmeldungenEntry(deleteDialog.id);
      setDeleteDialog({ open: false, id: null });
      onRefresh();
    } finally {
      setLoading(false);
    }
  };

  const handleToggleBezahlt = async (anmeldung: Anmeldungen) => {
    try {
      await LivingAppsService.updateAnmeldungenEntry(anmeldung.record_id, {
        bezahlt: !anmeldung.fields.bezahlt,
      });
      onRefresh();
    } catch (error) {
      console.error('Failed to update payment status:', error);
    }
  };

  const formatDate = (dateStr?: string) => {
    if (!dateStr) return '–';
    try {
      return format(parseISO(dateStr), 'dd.MM.yyyy', { locale: de });
    } catch {
      return dateStr;
    }
  };

  const getTeilnehmerName = (teilnehmerUrl?: string) => {
    const id = extractRecordId(teilnehmerUrl);
    if (!id) return '–';
    const t = teilnehmer.find(tn => tn.record_id === id);
    return t?.fields.name || '–';
  };

  const getKursName = (kursUrl?: string) => {
    const id = extractRecordId(kursUrl);
    if (!id) return '–';
    const k = kurse.find(ku => ku.record_id === id);
    return k?.fields.titel || '–';
  };

  const isFormValid = form.teilnehmer && form.kurs && form.anmeldedatum;

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold text-foreground">Anmeldungen</h2>
          <p className="text-sm text-muted-foreground">Verwalten Sie Kursanmeldungen</p>
        </div>
        <Button onClick={() => handleOpenDialog()}>
          <Plus className="size-4" />
          Anmeldung hinzufügen
        </Button>
      </div>

      {anmeldungen.length === 0 ? (
        <EmptyState
          icon={<ClipboardList className="size-8" />}
          title="Keine Anmeldungen"
          description="Erstellen Sie die erste Kursanmeldung."
          action={
            <Button onClick={() => handleOpenDialog()}>
              <Plus className="size-4" />
              Anmeldung hinzufügen
            </Button>
          }
        />
      ) : (
        <div className="data-table-container">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Teilnehmer</TableHead>
                <TableHead>Kurs</TableHead>
                <TableHead>Anmeldedatum</TableHead>
                <TableHead>Bezahlt</TableHead>
                <TableHead className="w-24"></TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {anmeldungen.map((a) => (
                <TableRow key={a.record_id}>
                  <TableCell className="font-medium">{getTeilnehmerName(a.fields.teilnehmer)}</TableCell>
                  <TableCell>{getKursName(a.fields.kurs)}</TableCell>
                  <TableCell className="text-muted-foreground">{formatDate(a.fields.anmeldedatum)}</TableCell>
                  <TableCell>
                    <button
                      onClick={() => handleToggleBezahlt(a)}
                      className="cursor-pointer"
                    >
                      {a.fields.bezahlt ? (
                        <span className="badge-success">
                          <Check className="size-3" />
                          Bezahlt
                        </span>
                      ) : (
                        <span className="badge-warning">
                          <X className="size-3" />
                          Offen
                        </span>
                      )}
                    </button>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1">
                      <Button variant="ghost" size="icon-sm" onClick={() => handleOpenDialog(a)}>
                        <Pencil className="size-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon-sm"
                        onClick={() => setDeleteDialog({ open: true, id: a.record_id })}
                      >
                        <Trash2 className="size-4 text-destructive" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}

      <Dialog open={dialogOpen} onOpenChange={(open) => { setDialogOpen(open); if (!open) resetForm(); }}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{editingId ? 'Anmeldung bearbeiten' : 'Neue Anmeldung'}</DialogTitle>
          </DialogHeader>
          <div className="form-section">
            <div className="space-y-2">
              <Label htmlFor="anmeldung-teilnehmer">Teilnehmer *</Label>
              <Select value={form.teilnehmer} onValueChange={(v) => setForm({ ...form, teilnehmer: v })}>
                <SelectTrigger id="anmeldung-teilnehmer">
                  <SelectValue placeholder="Teilnehmer auswählen" />
                </SelectTrigger>
                <SelectContent>
                  {teilnehmer.map((t) => (
                    <SelectItem key={t.record_id} value={t.record_id}>
                      {t.fields.name} ({t.fields.email})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="anmeldung-kurs">Kurs *</Label>
              <Select value={form.kurs} onValueChange={(v) => setForm({ ...form, kurs: v })}>
                <SelectTrigger id="anmeldung-kurs">
                  <SelectValue placeholder="Kurs auswählen" />
                </SelectTrigger>
                <SelectContent>
                  {kurse.map((k) => (
                    <SelectItem key={k.record_id} value={k.record_id}>
                      {k.fields.titel} ({formatDate(k.fields.startdatum)})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="anmeldung-datum">Anmeldedatum *</Label>
              <Input
                id="anmeldung-datum"
                type="date"
                value={form.anmeldedatum}
                onChange={(e) => setForm({ ...form, anmeldedatum: e.target.value })}
              />
            </div>
            <div className="flex items-center gap-2">
              <Checkbox
                id="anmeldung-bezahlt"
                checked={form.bezahlt}
                onCheckedChange={(checked) => setForm({ ...form, bezahlt: checked === true })}
              />
              <Label htmlFor="anmeldung-bezahlt" className="cursor-pointer">
                Bezahlt
              </Label>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setDialogOpen(false)}>Abbrechen</Button>
            <Button onClick={handleSubmit} disabled={loading || !isFormValid}>
              {loading ? 'Speichern...' : 'Speichern'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <ConfirmDialog
        open={deleteDialog.open}
        onOpenChange={(open) => setDeleteDialog({ open, id: open ? deleteDialog.id : null })}
        title="Anmeldung löschen"
        description="Möchten Sie diese Anmeldung wirklich löschen? Diese Aktion kann nicht rückgängig gemacht werden."
        onConfirm={handleDelete}
        loading={loading}
      />
    </div>
  );
}

